import React from "react";
import styled from "styled-components";
import Header from "./components/Header/Headers";
import Sidebar from "./components/Sidebar/Sidebar";
import MainContent from "./components/MainContent/MainContent";
import Footer from "./components/Footer/Footer";

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  min-height: 100vh;
`;

const ContentContainer = styled.div`
  display: flex;
  flex: 1;
  background-color: #1c1c1c;
`;

const news = [
  { id: '1', title: '뉴욕 연준 소비자 기대 설문조사', date: '6월 10일 11:06', source: 'financialjuice.com', tags: ['US Bonds', 'US Indexes', 'USD'], content: 'Detailed information about the 뉴욕 연준 소비자 기대 설문조사...' },
  // Add more news items as needed
];

function App() {
  const [expandedNewsId, setExpandedNewsId] = useState(null);

  const handleExpand = (id) => {
    setExpandedNewsId(expandedNewsId === id ? null : id);
  };

  return (
    <AppContainer>
      <Header />
      <ContentContainer>
        <Sidebar news={news} onExpand={handleExpand} />
        <MainContent news={news} expandedNewsId={expandedNewsId} onExpand={handleExpand} />
      </ContentContainer>
      <Footer />
    </AppContainer>
  );
}
export default App;
