import React from "react";
import styled from "styled-components";

const NewsItemContainer = styled.div`
  background-color: #282828;
  border-radius: 5px;
  padding: 15px;
  margin-bottom: 10px;
`;

const NewsTitle = styled.h3`
  margin: 0;
  font-size: 18px;
  color: #ff5e62;
`;

const NewsDetails = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 14px;
  color: #bbbbbb;
`;

const Tags = styled.div`
  display: flex;
  gap: 10px;
`;

const Tag = styled.span`
  background-color: #ff5e62;
  border-radius: 3px;
  padding: 3px 5px;
`;

function NewsItem({ title, date, source, tags }) {
  return (
    <NewsItemContainer>
      <NewsTitle>{title}</NewsTitle>
      <NewsDetails>
        <div>{date}</div>
        <div>{source}</div>
      </NewsDetails>
      <Tags>
        {tags.map((tag, index) => (
          <Tag key={index}>{tag}</Tag>
        ))}
      </Tags>
    </NewsItemContainer>
  );
}

export default NewsItem;
