import React from 'react';
import styled from 'styled-components';

const SidebarContainer = styled.div`
  background-color: #212121;
  color: white;
  width: 250px;
  padding: 20px;
`;

const NewsList = styled.div`
  display: flex;
  flex-direction: column;
`;

const NewsItem = styled.div`
  margin-bottom: 10px;
  cursor: pointer;
`;

const Sidebar = ({ news, onExpand }) => {
  return (
    <SidebarContainer>
      <h2>이슈 캘린더</h2>
      <NewsList>
        {news.map((item) => (
          <NewsItem key={item.id} onClick={() => onExpand(item.id)}>
            {item.title}
          </NewsItem>
        ))}
      </NewsList>
    </SidebarContainer>
  );
};

export default Sidebar;
