import React, { useEffect } from "react";
import styled from "styled-components";

const SidebarContainer = styled.div`
  background-color: #212121;
  color: white;
  width: 250px;
  padding: 20px;
  position: sticky;
  top: 0;
  height: 100vh;
  overflow-y: auto;
`;

const NewsList = styled.div`
  display: flex;
  flex-direction: column;
`;

const NewsItemContainer = styled.div`
  margin-bottom: 10px;
  cursor: pointer;
  padding: 5px;
  border-radius: 3px;
  background-color: ${({ $isActive }) => ($isActive ? "#444" : "transparent")};

  &:hover {
    background-color: #444;
  }

  &:nth-child(odd) {
    background-color: #333; /* Dark grey for odd items */
  }

  &:nth-child(even) {
    background-color: #2b2b2b; /* Light grey for even items */
  }
`;

const DateHeader = styled.div`
  color: #888;
  font-size: 14px;
  margin: 10px 0;
  border-bottom: 1px solid #444;
  padding-bottom: 5px;
`;

const NewsTitle = styled.div`
  color: white;
  font-size: 16px;
`;

const Sidebar = ({ news, onExpand, expandedNewsId, refs }) => {
  useEffect(() => {
    if (expandedNewsId) {
      const ref = refs.current.sidebar[expandedNewsId];
      if (ref) {
        ref.scrollIntoView({
          behavior: "smooth",
          block: "start",
        });
      }
    }
  }, [expandedNewsId, refs]);

  return (
    <SidebarContainer>
      <NewsList>
        {news.map((item, index) => (
          <React.Fragment key={item.id}>
            {index === 0 || item.date !== news[index - 1].date ? (
              <DateHeader>{item.date}</DateHeader>
            ) : null}
            <NewsItemContainer
              ref={(el) => (refs.current.sidebar[item.id] = el)}
              $isActive={item.id === expandedNewsId}
              onClick={() => onExpand(item.id)}
            >
              <NewsTitle>{item.title}</NewsTitle>
            </NewsItemContainer>
          </React.Fragment>
        ))}
      </NewsList>
    </SidebarContainer>
  );
};

export default Sidebar;
