import React, { useState, useRef, useCallback } from "react";
import styled from "styled-components";
import Header from "./components/Header/Headers";
import Sidebar from "./components/Sidebar/Sidebar";
import MainContent from "./components/MainContent/MainContent";
import Footer from "./components/Footer/Footer";

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  min-height: 100vh;
`;

const ContentContainer = styled.div`
  display: flex;
  flex: 1;
  background-color: #1c1c1c;
`;

const news = [
  // Your news data here
];

const additionalNews = Array.from({ length: 70 }, (_, i) => ({
  id: `${i + 4}`,
  title: `News Title ${i + 4}`,
  date: `Date ${i + 4}`,
  source: `Source ${i + 4}`,
  tags: ["Tag1", "Tag2"],
  content: `Detailed information about News Title ${i + 4}`,
  image: "https://via.placeholder.com/600x400",
}));

const allNews = [...news, ...additionalNews];

const debounce = (func, wait) => {
  let timeout;
  return (...args) => {
    clearTimeout(timeout);
    timeout = setTimeout(() => func(...args), wait);
  };
};

function App() {
  const [expandedNewsId, setExpandedNewsId] = useState(null);
  const newsRefs = useRef([]);
  const sidebarRefs = useRef([]);
  const mainContentRef = useRef();
  const sidebarRef = useRef();

  const scrollToView = useCallback(
    debounce((index, refs) => {
      if (index !== -1 && refs.current[index]) {
        refs.current[index].scrollIntoView({
          behavior: "smooth",
          block: "start",
        });
      }
    }, 100),
    []
  );

  const handleExpand = (id) => {
    setExpandedNewsId(expandedNewsId === id ? null : id);

    const mainContentIndex = allNews.findIndex((item) => item.id === id);
    const sidebarIndex = allNews.findIndex((item) => item.id === id);

    scrollToView(mainContentIndex, newsRefs);
    scrollToView(sidebarIndex, sidebarRefs);
  };

  return (
    <AppContainer>
      <Header />
      <ContentContainer>
        <Sidebar
          news={allNews}
          onExpand={handleExpand}
          expandedNewsId={expandedNewsId}
          sidebarRef={sidebarRef}
          sidebarRefs={sidebarRefs}
        />
        <MainContent
          news={allNews}
          expandedNewsId={expandedNewsId}
          onExpand={handleExpand}
          newsRefs={newsRefs}
          mainContentRef={mainContentRef}
        />
      </ContentContainer>
      <Footer />
    </AppContainer>
  );
}

export default App;
