import React, { useState, useRef, useEffect } from "react";
import styled from "styled-components";
import Header from "./components/Header/Headers";
import Sidebar from "./components/Sidebar/Sidebar";
import MainContent from "./components/MainContent/MainContent";
import Footer from "./components/Footer/Footer";
import { allNews } from "./components/newData";

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  min-height: 100vh;
`;

const ContentContainer = styled.div`
  display: flex;
  flex: 1;
  background-color: #1c1c1c;
  margin-top: 80px;
`;

const HeaderContainer = styled.div`
  position: fixed;
  top: 0;
  width: 100%;
  z-index: 1000;
`;

function App() {
  const [expandedNewsId, setExpandedNewsId] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const refs = useRef({ sidebar: {}, main: {} });

  const scrollToView = (id) => {
    const headerHeight = document.querySelector("header").offsetHeight;

    if (refs.current.sidebar[id]) {
      refs.current.sidebar[id].scrollIntoView({
        behavior: "smooth",
        block: "start",
      });
    }
    if (refs.current.main[id]) {
      refs.current.main[id].scrollIntoView({
        behavior: "smooth",
        block: "start",
      });
    }
    window.scrollBy(0, -headerHeight);
  };

  const handleExpand = (id) => {
    setExpandedNewsId(expandedNewsId === id ? null : id);
  };

  useEffect(() => {
    if (expandedNewsId) {
      scrollToView(expandedNewsId);
    }
  }, [expandedNewsId]);

  const newsPerPage = 100;
  const paginatedNews = allNews.slice((currentPage - 1) * newsPerPage, currentPage * newsPerPage);

  return (
    <AppContainer>
      <HeaderContainer>
        <Header />
      </HeaderContainer>
      <ContentContainer>
        <Sidebar
          news={paginatedNews}
          onExpand={handleExpand}
          expandedNewsId={expandedNewsId}
          refs={refs}
        />
        <MainContent
          news={paginatedNews}
          expandedNewsId={expandedNewsId}
          onExpand={handleExpand}
          refs={refs}
          currentPage={currentPage}
          setCurrentPage={setCurrentPage}
        />
      </ContentContainer>
      <Footer />
    </AppContainer>
  );
}

export default App;
