import React, { useEffect } from "react";
import styled from "styled-components";

const SidebarContainer = styled.div`
  background-color: #212121;
  color: white;
  width: 250px;
  padding: 20px;
  position: sticky;
  top: 0;
  height: 100vh;
  overflow-y: auto;
`;

const NewsList = styled.div`
  display: flex;
  flex-direction: column;
`;

const NewsItemContainer = styled.div`
  margin-bottom: 10px;
  cursor: pointer;
  background-color: ${({ $isActive }) => ($isActive ? "#444" : "transparent")};
  padding: 5px;
  border-radius: 3px;
  &:hover {
    background-color: #444;
  }
`;

const SectionHeader = styled.div`
  margin: 10px 0;
  font-weight: bold;
  color: #bbb;
  border-bottom: 1px solid #444;
  padding-bottom: 5px;
`;

const Sidebar = ({ news, onExpand, expandedNewsId, refs }) => {
  useEffect(() => {
    if (expandedNewsId) {
      const ref = refs.current.sidebar[expandedNewsId];
      if (ref) {
        setTimeout(() => {
          ref.scrollIntoView({
            behavior: "smooth",
            block: "start",
          });
        }, 100);
      }
    }
  }, [expandedNewsId, refs]);

  return (
    <SidebarContainer>
      <h2>이슈 캘린더</h2>
      <NewsList>
        {news.map((item, index) => (
          <React.Fragment key={item.id}>
            {index === 0 || item.date !== news[index - 1].date ? (
              <SectionHeader>{item.date}</SectionHeader>
            ) : null}
            <NewsItemContainer
              ref={(el) => (refs.current.sidebar[item.id] = el)}
              $isActive={item.id === expandedNewsId}
              onClick={() => onExpand(item.id)}
            >
              {item.title}
            </NewsItemContainer>
          </React.Fragment>
        ))}
      </NewsList>
    </SidebarContainer>
  );
};

export default Sidebar;
