import React, { useState, useRef, useEffect } from "react";
import styled from "styled-components";
import Header from "./components/Header/Headers";
import Sidebar from "./components/Sidebar/Sidebar";
import MainContent from "./components/MainContent/MainContent";
import Footer from "./components/Footer/Footer";
import { allNews } from "./components/newData";

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  min-height: 100vh;
`;

const ContentContainer = styled.div`
  display: flex;
  flex: 1;
  background-color: #1c1c1c;
`;

function App() {
  const [expandedNewsId, setExpandedNewsId] = useState(null);
  const sidebarRefs = useRef({});
  const mainRefs = useRef({});

  const scrollToSidebarView = (id) => {
    if (sidebarRefs.current[id]) {
      sidebarRefs.current[id].scrollIntoView({
        behavior: "smooth",
        block: "start",
      });
    }
  };

  const scrollToMainView = (id) => {
    if (mainRefs.current[id]) {
      mainRefs.current[id].scrollIntoView({
        behavior: "smooth",
        block: "start",
      });
    }
  };

  const handleExpand = (id) => {
    setExpandedNewsId(expandedNewsId === id ? null : id);
    setTimeout(() => {
      scrollToMainView(id);
      scrollToSidebarView(id);
    }, 100); // Adding a slight delay to ensure scroll execution
  };

  useEffect(() => {
    if (expandedNewsId) {
      scrollToMainView(expandedNewsId);
      scrollToSidebarView(expandedNewsId);
    }
  }, [expandedNewsId]);

  return (
    <AppContainer>
      <Header />
      <ContentContainer>
        <Sidebar
          news={allNews}
          onExpand={handleExpand}
          expandedNewsId={expandedNewsId}
          refs={sidebarRefs}
        />
        <MainContent
          news={allNews}
          expandedNewsId={expandedNewsId}
          onExpand={handleExpand}
          refs={mainRefs}
        />
      </ContentContainer>
      <Footer />
    </AppContainer>
  );
}

export default App;
